import { times } from "./src/utils";

const sinePattern = new Pattern((x, y, elapsed) => {
  const modifier = Math.sin(elapsed / 100);
  return {
    x: x + modifier,
    y: y + modifier,
  };
});

const spreadPattern = new Pattern((x, y, elapsed, index) => {
  return {
    x: x + index / 10,
    y: y,
  };
});

function Bullet(x, y) {
  this.x = x || 0;
  this.y = y || 0;
  this.ctx;

  this.offscreen = function () {
    return this.x > can.width || this.y > can.height;
  };

  this.initialize = function (ctx) {
    this.ctx = ctx;
    this.update();
  };

  this.update = function () {
    this.ctx.beginPath();
    this.ctx.arc(this.x, this.y, 5, 0, 2 * Math.PI);

    this.ctx.fillStyle = "rgba(250,0,0,0.4)";
    this.ctx.fill();
  };
}

function BulletGroup(x, y, angle) {
  this.bullets = [];
  this.pattern;
  this.ctx;
  this.amount = 0;
  this.x = x;
  this.y = y;
  this.angle = angle;

  this.setAngle = (angle) => {
    this.angle = angle;
  };

  this.setPattern = (pattern) => {
    this.pattern = pattern;
  };

  this.generateBullets = (amount) => {
    this.amount = amount;
    times(amount)(() => {
      this.bullets.push(new Bullet(this.x, this.y));
    });
  };

  this.initialize = (ctx) => {
    this.ctx = ctx;
    this.bullets.forEach((bullet) => bullet.initialize(ctx));
  };

  this.update = (elapsed) => {
    this.bullets.forEach((bullet, index) => {
      if (bullet.offscreen()) {
        this.bullets.splice(index, 1);
        return;
      }

      // TODO change this name
      const absoluteIndex = index - this.amount / 2;
      const updates = this.pattern.updatePosition(
        bullet.x,
        bullet.y,
        elapsed,
        absoluteIndex,
        this.angle
      );

      // Fire and move according to the angle
      bullet.x = updates.x + Math.sin(this.angle * (Math.PI / 180));
      bullet.y = updates.y + Math.cos(this.angle * (Math.PI / 180));
      bullet.update();
    });
  };
}

function BulletManager(x, y, angle) {
  this.bulletGroups = [];
  this.x = x || can.width / 2;
  this.y = y || can.height / 2;
  this.baseAngle = angle;
  let timestamp;

  this.createBulletGroup = function () {
    const bullets = new BulletGroup(this.x, this.y, this.angle);
    bullets.generateBullets(6);
    bullets.initialize(ctx);
    bullets.setPattern(spreadPattern);
    this.bulletGroups.push(bullets);
  };

  this.calculateAngle = function (elapsed) {
    const width = 45;
    const min = 270 + 45 + 45 / 2;

    // Oscillates between min angle and max angle (max = min + width)
    this.angle = this.baseAngle + min + (Math.sin(elapsed) + 1) * (width / 2);
  };

  this.drawLine = function () {
    var ctx = can.getContext("2d");
    ctx.translate(this.x, this.y);
    ctx.fillStyle = "blue";

    ctx.rotate(this.angle * -(Math.PI / 180));

    ctx.fillRect(0, 0, 5, 150);
    ctx.setTransform(1, 0, 0, 1, 0, 0);
  };

  this.drawLine();

  this.update = function (elapsed) {
    timestamp = timestamp || elapsed || 0;
    // How fast to spin
    const speed = 500;
    this.calculateAngle(elapsed / speed);
    this.drawLine();
    this.bulletGroups.forEach((group, index) => {
      if (group.bullets.length === 0) {
        // Could make this integrated into the manager
        this.bulletGroups.splice(index, 1);
        return;
      }
      group.update(elapsed / speed);
    });
  };
}

// Pattern is a function that takes x and y
function Pattern(pattern) {
  this.pattern = pattern;
  this.updatePosition = function (x, y, elapsed, index, angle) {
    return pattern(x, y, elapsed, index, angle);
  };
}

var can = document.getElementById("canvas");
can.height = 1000;
can.width = 1300;
var ctx = can.getContext("2d");
let previousTimestamp,
  start,
  dt = 0,
  secondCounter = 0;

// TODO Fix times
let managers = [];
const amt = 1;
times(amt)((i) => {
  const angle = i * (360 / amt);
  managers.push(new BulletManager(undefined, undefined, angle));
});

managers.push(new BulletManager(can.width / 4, can.height / 3, 0));
managers.push(new BulletManager(can.width - can.width / 4, can.height / 3, 0));

// Pulses shots once every second
let paused = false;
// setInterval(() => {
//   paused = paused ? false : true;
// }, 500);

function update(timestamp) {
  ctx.clearRect(0, 0, can.width, can.height);
  if (start === undefined) start = timestamp;
  if (previousTimestamp === undefined) previousTimestamp = timestamp;

  const elapsed = timestamp - start;
  // fix this jank
  dt = timestamp - previousTimestamp;
  secondCounter = dt ? dt + secondCounter : 0;
  previousTimestamp = timestamp;

  managers.forEach((manager, i) => {
    // Move to the manager if possible
    if (secondCounter >= 100 && !paused) {
      manager.createBulletGroup();
      if (i === managers.length - 1) {
        secondCounter = 0;
      }
    }
    manager.update(elapsed);
  });

  requestAnimationFrame(update);
}

update();
